parameters.functionScript:
let userid = Inputs.userid || '';
const yearValue = Inputs.yearValue;
const archived = true;

try {
  // Retrieve records from the 'categories' table
  const { data, error } = await Noodl.Variables.supabaselocal.from('participation')
  .select('*')
  .eq('user_id', userid)
  .eq('is_active', archived)
  .eq('schoolyear', yearValue)
  .order('created_at', { ascending: false })
  ;

  // Create a JSON array to store the rows
  const jsonArray = [];
  if(data){
    data.forEach(row => {
      const jsonRow = {
        Id: row.id,
        Created_at: row.created_at,
        User_id: row.user_id,
        Activity_id: row.activity_id,
        Noi_id: row.noi_id,
        Is_institutional: row.is_institutional,
        Is_verified: row.is_verified,
        Schoolyear: row.schoolyear,
        Is_active: row.is_active,
        Remarks: row.remarks
      };
      jsonArray.push(jsonRow);
    });
  }
  // Iterate through the rows and assign 'desc' to 'Label' and 'id' to 'Value'
  if (error){
    console.log(error);
  }
  
  // Output the JSON array
  Outputs.JsonArray = jsonArray;
  console.log("[fetch participation Success] : ", jsonArray);
  Outputs.Success();

  
} catch (error) {
  console.log("getParticipations: ERROR : ", error);
  Outputs.Error = error;
  Outputs.Failure();
  return;
}

const partid = Inputs.id || '';
const isApproved = Inputs.isApproved;


if (isApproved){
    Outputs.errorMessage = "You cannot delete an approved record.";
    Outputs.Failure();
    return;
} else if (isApproved === null) {
    Outputs.errorMessage = "You cannot delete an approved record for resubmission.";
    Outputs.Failure();
    return;
}

