let cat_id = Inputs.cat_id || '';

try {
  // Retrieve records from the 'categories' table
  const { data, error } = await Noodl.Variables.supabaselocal.from('activities')
  .select('id, cat_id, desc')
  .eq('cat_id', cat_id)
  ;

  // Create a JSON array to store the rows
  const jsonArray = [];

  // Iterate through the rows and assign 'desc' to 'Label' and 'id' to 'Value'
  data.forEach(row => {
    const jsonRow = {
      Label: row.desc,
      Value: row.id
    };
    jsonArray.push(jsonRow);
  });

  // Output the JSON array
  Outputs.JsonArray = jsonArray;
  Outputs.Success();

  
} catch (error) {
  console.log("getActivities: ERROR : ", error);
  Outputs.Error = error;
  Outputs.Failure();
  return;
}

