const _id = Inputs.id || null;
const userid = Inputs.userid || null;
const firstname = Inputs.firstname || null;
const lastname = Inputs.lastname || null;
const officeid = Inputs.officeid || null;
const is_PhD = Inputs.is_PhD;
const isFaculty = Inputs.isFaculty || null; //check if the check box was checked

try {
    const profileData = {
        id: _id,
        user_id: userid,
        first_name: firstname,
        last_name: lastname,
        is_phd: is_PhD
    };

    Object.keys(profileData).forEach(key => {
        if (profileData[key] === null) {
            delete profileData[key];
        }
    });

    if (isFaculty) {
        profileData.dep_id = officeid;
        profileData.off_id = null;
    } else {
        profileData.off_id = officeid;
        profileData.dep_id = null;
    }

    const { data, error } = await Noodl.Variables.supabase
        .from('profiles')
        .upsert([profileData])
        .select();

    if (error) {
        console.log("Error Upserting Profile: ", error);
        Outputs.Failure();
    } else {
        console.log("Profile Upserted Successfully");
        Outputs.Success();
    }
} catch (e) {
    console.log("ERROR ADDING NEW USER PROFILE", e);
    Outputs.Failure();
}
